targets.source:
(
  100 * (
    max by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:net:speed_recv{
          cluster_domain="$cluster_domain",
          device_name="eth1",
          instance_role=~"$instance_role"
        }

    ) +
    max by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:net:speed_sent{
          cluster_domain="$cluster_domain",
          device_name="eth1",
          instance_role=~"$instance_role"
        }

    )
  ) /  (96 * 1024 * 1024)  / 
  sum by(ip, bk_target_cloud_id, instance_role) (
       bkmonitor:dbm_system:cpu_detail:usage{
        cluster_domain="$cluster_domain",
        instance_role=~"$instance_role"
      } 
  )   and
    sum by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:cpu_detail:usage{
          cluster_domain="$cluster_domain",
          instance_role=~"$instance_role"
        } 
    ) <= 2
   or
  (
    100 * (
      max by(ip, bk_target_cloud_id, instance_role) (
          bkmonitor:dbm_system:net:speed_recv{
            cluster_domain="$cluster_domain",
            device_name="eth1",
            instance_role=~"$instance_role"
          } 
      ) +
      max by(ip, bk_target_cloud_id, instance_role) (
          bkmonitor:dbm_system:net:speed_sent{
            cluster_domain="$cluster_domain",
            device_name="eth1",
            instance_role=~"$instance_role"
          } 
      )
    ) / (48 * 1024 * 1024)/
    sum by(ip, bk_target_cloud_id, instance_role) (
         bkmonitor:dbm_system:cpu_detail:usage{
          cluster_domain="$cluster_domain",
          instance_role=~"$instance_role"
        } 
    )  and 

    sum by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:cpu_detail:usage{
          cluster_domain="$cluster_domain",
          instance_role=~"$instance_role"
        } 
    ) > 2
  )
)

(
  100 * (
    max by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:net:speed_packets_recv{
          cluster_domain="$cluster_domain",
          device_name="eth1",
          instance_role=~"$instance_role"
        }

    ) +
    max by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:net:speed_packets_sent{
          cluster_domain="$cluster_domain",
          device_name="eth1",
          instance_role=~"$instance_role"
        }

    )
  ) /  (96 * 1024 * 1024)  /
  sum by(ip, bk_target_cloud_id, instance_role) (
       bkmonitor:dbm_system:cpu_detail:usage{
        cluster_domain="$cluster_domain",
        instance_role=~"$instance_role"
      }
  )   and
    sum by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:cpu_detail:usage{
          cluster_domain="$cluster_domain",
          instance_role=~"$instance_role"
        }
    ) <= 2
   or
  (
    100 * (
      max by(ip, bk_target_cloud_id, instance_role) (
          bkmonitor:dbm_system:net:speed_packets_recv{
            cluster_domain="$cluster_domain",
            device_name="eth1",
            instance_role=~"$instance_role"
          }
      ) +
      max by(ip, bk_target_cloud_id, instance_role) (
          bkmonitor:dbm_system:net:speed_packets_sent{
            cluster_domain="$cluster_domain",
            device_name="eth1",
            instance_role=~"$instance_role"
          }
      )
    ) / (48 * 1024 * 1024)/
    sum by(ip, bk_target_cloud_id, instance_role) (
         bkmonitor:dbm_system:cpu_detail:usage{
          cluster_domain="$cluster_domain",
          instance_role=~"$instance_role"
        }
    )  and

    sum by(ip, bk_target_cloud_id, instance_role) (
        bkmonitor:dbm_system:cpu_detail:usage{
          cluster_domain="$cluster_domain",
          instance_role=~"$instance_role"
        }
    ) > 2
  )
)
